import { Component } from '@angular/core';

@Component({
  selector: 'app-counter',
  templateUrl: './counter.component.html',
  styleUrls: ['./counter.component.css']
})
export class CounterComponent {
  counter: number = 0;
    
  onIncrement(): void {
    this.counter++;
  }
  onDecrement(): void {
    this.counter--;
  }
  onReset(): void {
    this.counter=0;
  }
}
<div class="row">
  <div class="col-md-12">
    <div class="my-3">
      <app-counter-output [counter]="counter"></app-counter-output>
    </div>
    <div class="my-3">
      <app-counter1-buttons (increment)="onIncrement()" (decrement)="onDecrement()" (reset)="onReset()"></app-counter1-buttons>
    </div>
  </div>
</div>
